import { useState, useEffect } from "react";

const gifts = [
  {
    name: "Iphone",
    image:
      "data:image/jpeg;base64,",
  },

  {
    name: "Macbook",
    image:
      "data:image/jpeg;base64,",
  },
  {
    name: "Iphone",
    image:
      "data:image/jpeg;base64,",
  },

  {
    name: "Macbook",
    image:
      "data:image/jpeg;base64,",
  },
  {
    name: "Iphone",
    image:
      "data:image/jpeg;base64,",
  },

  {
    name: "Macbook",
    image:
      "data:image/jpeg;base64,",
  },
  {
    name: "Iphone",
    image:
      "data:image/jpeg;base64,",
  },
  {
    name: "Iphone",
    image:
      "data:image/jpeg;base64,",
  },
  {
    name: "Iphone",
    image:
      "data:image/jpeg;base64,",
  },
];

const getRandomGift = () => gifts[Math.floor(Math.random() * gifts.length)];
const generateGrid = () =>
  Array(9)
    .fill(null)
    .map(() => getRandomGift());

export default function GiftBoxGame() {
  const [grid, setGrid] = useState(generateGrid());
  const [flipped, setFlipped] = useState(Array(9).fill(false));
  const [selected, setSelected] = useState([]);
  const [attempts, setAttempts] = useState(0);
  const [message, setMessage] = useState("");
  const [showDialog, setShowDialog] = useState(false);

  useEffect(() => {
    if (selected.length === 3) {
      if (selected.every((val) => val.name === selected[0].name)) {
        setMessage(`You won a ${selected[0].name}! 🎉`);
      } else {
        if (attempts < 1) {
          setMessage("Try again! One more chance left.");
          setTimeout(() => {
            setFlipped(Array(9).fill(false));
            setSelected([]);
          }, 1500);
        } else {
          setMessage("Better luck next time! ❌");
        }
        setAttempts(attempts + 1);
      }
      setShowDialog(true);
    }
  }, [selected]);

  const handleBoxClick = (index) => {
    if (flipped[index] || selected.length >= 3) return;
    const newFlipped = [...flipped];
    newFlipped[index] = true;
    setFlipped(newFlipped);
    setSelected([...selected, grid[index]]);
  };

  return (
      <>
      <br />
      <div style={{width: "400px"}} className='cooperation-div z-20 text-center'>
      <h1 className="cooperation-text text-3xl font-bold mb-4 text-blue-500"> Get a chance to win <br />Iphone and Macbook</h1>
      </div>
      <br /><br />
      <div className="grid grid-cols-3 gap-4">
        {grid.map((gift, index) => (
          <div
            key={index}
            className={`w-24 h-24 overflow-hidden bg-blue-100 flex items-center justify-center text-white text-2xl rounded-xl shadow-lg cursor-pointer transition-transform duration-500 ${
              flipped[index] ? "rotate-y-180" : ""
            }`}
            onClick={() => handleBoxClick(index)}
          >
            {flipped[index] ? (
              <img src={gift.image} alt={gift.name} className="h-full object-cover" />
            ) : (
              <p className="scale-[200%]">🎁</p>
            )}
          </div>
        ))}
      </div>

      {showDialog && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 transition-opacity duration-300 animate-fade-in">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center transform transition-transform duration-300 animate-scale-in">
            <p className="text-lg font-semibold">{message}</p>
            <button
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
              onClick={() => setShowDialog(false)}
            >
              OK
            </button>
          </div>
        </div>
      )}
        <br /><br />
      </>
  );
}

